import react, { useState, useEffect } from "react";
import axios from "axios";

import "./App.modules.css";
import { toggleTodoStatus } from "../../backend/controllers/todoController";
const BASE_URL = "http://localhost:5000/api";

function App() {
  const [todos, setTodos] = useState(null);
  const [todo, setTodo] = useState("");

  useEffect(() => {
    getTodos();
  }, []);

  const getTodos = () => {
    axios
      .get(`${BASE_URL}/todos`)
      .get((res) => setTodos(res.data))
      .catch((error) => console.error(err));
  };

  const handleAddTodo = () => {
    axios
      .post(`${BASE_URL}/todo/new`, {
        title: "todo",
      })
      .then((res) => {
        setTodos([...todos, res.data]);
        setTodos("");
      })
      .catch((err) => console.error(err));
  };

  const handleDeleteTodo = (id) => {
    axios
      .delete(`${BASE_URL}/todo/delete/${id}`)
      .then((res) =>
        setTodos(todos.filter((todo) => todo.id !== res.data._id)).catch(
          (err) => console.error(err)
        )
      );
  };

  const handleTodoClick = (id) => {
    axios
      .get(`${BASE_URL}/todo/toggleStatus/${id}`)
      .then((res) => getTodos())
      .catch((err) => console.error(err));
  };

  return (
    <div className="App">
      <div className="todo-input-wrapper">
        <input
          className="todo-input-bar"
          value={todo}
          onChange={(e) => setTodos(e.target.value)}
          placeholder="Add a new todo...."
        />
      </div>
      <div className="add-button" onClick={handleAddTodo}>
        +
      </div>
    </div>
  );
}

export default App;
